import { Component, OnInit } from '@angular/core';
import { FormGroup,FormControl } from '@angular/forms';
import { Router } from '@angular/router';
import { map } from 'rxjs';
import { Airport } from '../airport';
import { Flight } from '../flight';
import { FlightService } from '../flight.service';

@Component({
  selector: 'app-flight-search',
  templateUrl: './flight-search.component.html',
  styleUrls: ['./flight-search.component.css']
})
export class FlightSearchComponent implements OnInit {

  flightRef = new FormGroup({
    flighttype:new FormControl(),
    fromCity:new FormControl(),
    toCity:new FormControl(),
    departingDate:new FormControl(),
    adults:new FormControl(),
    children:new FormControl(),
    travelClass:new FormControl()
  })
  msg:string="";
  flightData:Array<Flight>=[];
  constructor(public router:Router,public fs:FlightService) { }

  ngOnInit(): void {
    let i=0;
    this.fs.loadFlightDetails().pipe(map((e:any)=>{
        // code to set random date for flights 
         e.flights.map((ee:any)=>{
          const minValue = new Date(2023,0,1).getTime();
          const maxValue = new Date(2023,0,31).getTime();
          const timestamp = Math.floor(Math.random() * (maxValue - minValue + 1) + minValue);
          ee.flightDate=new Date(timestamp).toISOString().slice(0, 10)
        })
        return e;
      }
      )).subscribe({
      next:(data:any)=> {
          console.log(data);
      },
      error:(error:any)=> {
        console.log(error);
      },
      complete:()=> console.log("Done!")
    })

  }

  async searchFlight(){

    let flight = this.flightRef.value;
    console.log(flight);
    let currentDate = new Date().toISOString().slice(0, 10);
    let yourSelectedDate = flight.departingDate;
    if(currentDate>yourSelectedDate){
      this.msg="Plz select current or future date!"
    }else {
      console.log(flight.fromCity)
      let result = this.flightData.length;
      console.log(result);  
      //let searchedFlights = await this.flightData.filter(ff=>ff.origin==flight.fromCity && ff.destination==flight.toCity)
        //console.log(searchedFlights)
        //this.router.navigate(["flight-searched-result"]);
        //this.flightRef.reset();
    }


  }
}
<div id="booking" class="section">
    <div class="section-center">
        <div class="container">
            <div class="row">
                <div class="col-md-4">
                    <div class="booking-cta">
                        <h3>Book your flight today</h3>
                    </div>
                </div>
                <div class="col-md-7 col-md-offset-1">
                    <div class="booking-form">
                        <form [formGroup]="flightRef">
                            <div class="form-group">
                                <div class="form-checkbox">

                                    <label for="one-way">
                                        <input type="radio" id="one-way" name="flighttype" formControlName="flighttype" value="oneway">
                                        <span></span>One way
                                    </label>
                                </div>
                            </div>
                            <div class="row">
                                <div class="col-md-6">
                                    <div class="form-group">
                                        <span class="form-label">Flying from</span>
                                        <input class="form-control" type="text" placeholder="City or airport" name="fromCity" formControlName="fromCity">
                                    </div>
                                </div>
                                <div class="col-md-6">
                                    <div class="form-group">
                                        <span class="form-label">Flyning to</span>
                                        <input class="form-control" type="text" placeholder="City or airport" name="toCity" formControlName="toCity">
                                    </div>
                                </div>
                            </div>
                            <div class="row">
                                <div class="col-md-6">
                                    <div class="form-group">
                                        <span class="form-label">Departing</span>
                                        <input class="form-control" type="date" required formControlName="departingDate" id="datepicker">
                                    </div>
                                </div>

                            </div>
                            <div class="row">
                                <div class="col-md-4">
                                    <div class="form-group">
                                        <span class="form-label">Adults (18+)</span>
                                        <select class="form-control" formControlName="adults">
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                            <option value="3">3</option>
                                        </select>
                                        <span class="select-arrow"></span>
                                    </div>
                                </div>
                                <div class="col-md-4">
                                    <div class="form-group">
                                        <span class="form-label">Children (0-17)</span>
                                        <select class="form-control" formControlName="children">
                                            <option value="0">0</option>
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                        </select>
                                        <span class="select-arrow"></span>
                                    </div>
                                </div>
                                <div class="col-md-4">
                                    <div class="form-group">
                                        <span class="form-label">Travel class</span>
                                        <select class="form-control" formControlName="travelClass">
                                            <option value="economy class">Economy class</option>
                                            <option value="business class">Business class</option>
                                            <option value="first class">First class</option>
                                        </select>
                                        <span class="select-arrow"></span>
                                    </div>
                                </div>
                            </div>
                            <div class="form-btn">
                                <button class="submit-btn" (click)="searchFlight()">Show flights</button>
                            </div>
                        </form>
                        <span style="color:red;">{{msg}}</span>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
